import React, {Component} from 'react';
import {Button, Form, Input, Layout, Menu, Table} from 'antd';
import logo from './images/ghlogo.png';
import './App.css';
import axios from 'axios';
const { Header, Content, Footer } = Layout;

const api = {
  baseUrl : "https://api.github.com",
  client_id : "3e550c2ad7e4c5a2",
  client_secret : "7c7a9ecaf707856bfb4e4419680070b85099f4c5"
}

class Dashboard extends Component{
  constructor(){
    super();
    this.state = {
      dataSource: [],
      dataDetails: [],
      menu: '1',
      user: "",
      details: false,
    }
  }

  componentDidMount(){
    axios.get(api.baseUrl
        + "/repos/octokit/octokit.rb/issues"
      )
      .then((res) => {
        console.log(res);
      })
  }

  showMenu = key =>{
    this.setState({
      menu : key
    });
  }
  showDetails = record =>{
    console.log(record);
    var details = [];
    var dataDetails = [];
    axios.get(api.baseUrl
        + "/repos/"+ this.state.user
        + "/" +record.repo + "/issues"
      )
      .then((res) => {
        details = res;
        console.log(res);
      });
    for (var i = 0; i < parseInt(details.length, 10); i++) {
        dataDetails.push({
            repo: details[i].name,
        });
    }
    this.setState({
      details : true
    });

  }

  onFinish = values => {
    console.log(values);
    axios.get(api.baseUrl
        + "/users/" + values.user + "/repos"
      )
      .then((res) => {
        console.log(res);
        this.setState({
          dataSource : res.data,
          user: values.user,
        })
      })


  };

  onFinishO = values => {
    console.log(values);
    axios.get(api.baseUrl
        + "/orgs/" + values.user + "/repos"
      )
      .then((res) => {
        console.log(res);
        this.setState({
          dataSource : res.data,
          user: values.user,
        })
      })
  };

  render(){
    var dataSource = [];
    var data = this.state.dataSource;
    for (var i = 0; i < parseInt(data.length, 10); i++) {
        dataSource.push({
            repo: data[i].name,
            details: 'See More'
        });
    }

      const columns = [
        {
          title: 'Repository',
          dataIndex: 'repo',
          key: 'repo',
        },
        {
          title: 'Details',
          dataIndex: 'details',
          key: 'details',
          render: (text, record) => <a onClick={()=> this.showDetails(record)}>{text}</a>,
        },
      ];
    return (
       <Layout >
       <Header style={{ padding: '0 50px', minHeight: '120px' }}>

       <img src={logo} alt="Logo" style={{ width: '100px', float: 'left', margin: '16px 24px 16px 0' }}/>
          <Menu theme="dark" mode="horizontal" defaultSelectedKeys={['1']} style={{ margin: '16px 24px 16px 0'  }}>

            <Menu.Item key="1" onClick={(e) => this.showMenu(e.key)} >Users</Menu.Item>
            <Menu.Item key="2" onClick={(e) => this.showMenu(e.key)} >Organizations</Menu.Item>
          </Menu>
        </Header>
          <Content style={{ padding: '0 50px', minHeight: '440px' }}>
          {this.state.menu === '1'?
          (<Form
              onFinish={this.onFinish}>
                <Form.Item
                  name="user"
                  label="Username"
                  style={{ paddingTop: 10 }}>
                  <Input />
                </Form.Item>
                {/*
                <Form.Item>
                </Form.Item>
                */}
                <Form.Item  style={{ paddingTop: 5 }}>
                  <Button type="primary" htmlType="submit">
                    Confirmar
                  </Button>
                </Form.Item>
            </Form>):(
              <Form
                  onFinish={this.onFinishO}>
                    <Form.Item
                      name="user"
                      label="Organization name"
                      style={{ paddingTop: 10 }}>
                      <Input />
                    </Form.Item>
                    {/*
                    <Form.Item>
                    </Form.Item>
                    */}
                    <Form.Item  style={{ paddingTop: 5 }}>
                      <Button type="primary" htmlType="submit">
                        Confirmar
                      </Button>
                    </Form.Item>
                </Form>
            )
          }
          <Table dataSource={dataSource} columns={columns} />
          </Content>
        <Footer style={{ textAlign: 'center' }}>Beatricce ©2020</Footer>

        </Layout>
    )
  }
}

function App() {
  return (
    <div className="App">
      <Dashboard />
    </div>
  );
}

export default App;
