import React, {Component} from 'react';
import logo from './images/ghlogo.jpg';
import './App.css';
import axios from 'axios';
import {Button, Form, Input, Layout, Menu} from 'antd';

const { Header, Content, Footer } = Layout;



/*
+ "&client_id="
+ api.client_id
+ "&client_secret="
+ api.client_secret
*/

const api = {
  baseUrl : "https://api.github.com",
  client_id : "3e550c2ad7e4c5a2",
  client_secret : "7c7a9ecaf707856bfb4e4419680070b85099f4c5"
}

class Dashboard extends Component{
  constructor(){
    super();
    this.state = {
      dataSource: [],
      menu: 1,
      user: "",
      org: "",
    }
  }

  /*componentDidMount() {
    axios.get(api.baseUrl
        + "/users/Beatricce/repos"
      )
      .then((res) => {
        console.log(res);
        this.setState({
          dataSource : res.data
        })
      })
  }*/
  showMenu = key =>{
    this.setState({
      menu : key
    })
  }

  onFinish = values => {
    console.log(values);
    axios.get(api.baseUrl
        + "/users/" + values.user + "/repos"
      )
      .then((res) => {
        console.log(res);
        this.setState({
          dataSource : res.data
        })
      })
  };

  render(){
    return (
      <div >
       <Layout className="layout">
       <Header>
          <img src={logo} alt="Logo" />
          <div className="logo" />
          <Menu theme="dark" mode="horizontal" defaultSelectedKeys={['1']}>
            <Menu.Item key="1" onClick={(e) => this.showMenu(e.key)} >Users</Menu.Item>
            <Menu.Item key="2" onClick={(e) => this.showMenu(e.key)} >Organizations</Menu.Item>
          </Menu>
        </Header>
        <Content style={{ padding: '0 50px' }}>

        </Content>
        <Footer style={{ textAlign: 'center' }}>Beatricce ©2020</Footer>
        <Form
            onFinish={this.onFinish}>
              <Form.Item
                name="user"
                label="Username"
                style={{ paddingTop: 10 }}>
                <Input />
              </Form.Item>
              {/*
              <Form.Item>
              </Form.Item>
              */}
              <Form.Item  style={{ paddingTop: 5 }}>
                <Button type="primary" htmlType="submit">
                  Confirmar
                </Button>
              </Form.Item>
          </Form>
        </Layout>
      </div>
    );
  }
}

function App() {
  return (
    <div className="App">
      <Dashboard />
    </div>
  );
}

export default App;
