import React, {Component} from 'react';
import logo from './logo.svg';
import './App.css';
import axios from 'axios';
import {Button, Form, Input, Layout, Menu} from 'antd';

/*
+ "&client_id="
+ api.client_id
+ "&client_secret="
+ api.client_secret
*/

const api = {
  baseUrl : "https://api.github.com",
  client_id : "3e550c2ad7e4c5a2",
  client_secret : "7c7a9ecaf707856bfb4e4419680070b85099f4c5"
}

class Dashboard extends Component{
  constructor(){
    super();
    this.state = {
      dataSource: [],
      user: "",
      org: "",
    }
  }

  /*componentDidMount() {
    axios.get(api.baseUrl
        + "/users/Beatricce/repos"
      )
      .then((res) => {
        console.log(res);
        this.setState({
          dataSource : res.data
        })
      })
  }*/

  onFinish = values => {
    console.log(values);
    axios.get(api.baseUrl
        + "/users/" + values.user + "/repos"
      )
      .then((res) => {
        console.log(res);
        this.setState({
          dataSource : res.data
        })
      })
  };

  render(){
    return (
      <div >
      <Form
          onFinish={this.onFinish}>
            <Form.Item
              name="user"
              label="Username"
              style={{ paddingTop: 10 }}>
              <Input />
            </Form.Item>
            {/*
            <Form.Item>
            </Form.Item>
            */}
            <Form.Item  style={{ paddingTop: 5 }}>
              <Button type="primary" htmlType="submit">
                Confirmar
              </Button>
            </Form.Item>
          </Form>
      </div>
    );
  }
}

function App() {
  return (
    <div className="App">
      <Dashboard />
    </div>
  );
}

export default App;
