+ api.client_id
+ "&client_secret="
+ api.client_secret
*/

const api = {
  baseUrl : "https://api.github.com",
  client_id : "3e550c2ad7e4c5a2",
  client_secret : "7c7a9ecaf707856bfb4e4419680070b85099f4c5"
}

class Dashboard extends Component{
  constructor(){
    super();
    this.state = {
      dataSource: [],
      user: "",
      org: "",
    }
  }

  componentDidMount() {
    axios.get(api.baseUrl
        + "/users/Beatricce/repos"
      )
      .then((res) => {
        console.log(res);
        this.setState({
          dataSource : res.data
        })
      })
  }

  onFinish = values => {
