            */}
            <Form.Item  style={{ paddingTop: 10 }}>
              <Button htmlType="button" onClick={this.cancelSubmit}>
                Cancelar
              </Button>
            </Form.Item>
            <Form.Item  style={{ paddingTop: 5 }}>
              <Button type="primary" htmlType="submit">
                Confirmar
              </Button>
            </Form.Item>
          </Form>
      </div>
    );
  }
}

function App() {
  return (
    <div className="App">
      <Dashboard />
    </div>
  );
}

export default App;
